  handleScroll = e => {
    const scrollTop = e.target.scrollTop;
    const sections = this.state.sections;
    const lastIndex = sections.length - 1;

    for (let i = 0; i < lastIndex; i++) {
      if (
        scrollTop > sections[i].offsetTop - 20 &&
        scrollTop < sections[i + 1].offsetTop - 20
